import React from 'react'

function Choose() {
  const a = (cl) => document.querySelector(cl)

  const sinalsoma = () => {
    a('.sinal').innerHTML = '+'
  }

  const sinalsubt = () => {
    a('.sinal').innerHTML = '-'
  }

  const sinaldivi = () => {
    a('.sinal').innerHTML = '/'
  }

  const sinalmulti = () => {
    a('.sinal').innerHTML = 'x'
  }

  return (
    <div className='choose-sinal'>
      <button onClick={sinalsoma}>SOMA</button>
      <input type='radio' name='sinais1' onClick={sinalsubt} />
      <input type='radio' name='sinais1' onClick={sinaldivi} />
      <input type='radio' name='sinais1' onClick={sinalmulti} />
    </div>
  )
}

export default Choose
