import React from 'react'

function Choose() {
  const a = (cl) => document.querySelector(cl)
  const act = (cl) => {
    a(cl).style.background = 'green'
    a(cl).style.color = 'white'
  }



  const sinalsoma = () => {
    a('.sinal').innerHTML = '+'

    a('.sinalsoma').setAttribute('id')
    act('.sinalsoma')
    /*a('.sinalsoma').style.background = 'green'
    a('.sinalsoma').style.color = 'white'*/
  }

  const sinalsubt = () => {
    a('.sinal').innerHTML = '-'
    /*a('.sinalsubt').style.background = 'green'
    a('.sinalsubt').style.color = 'white'*/
  }

  const sinaldivi = () => {
    a('.sinal').innerHTML = '/'
    /*a('.sinaldivi').style.background = 'green'
    a('.sinaldivi').style.color = 'white'*/
  }

  const sinalmulti = () => {
    a('.sinal').innerHTML = 'x'
    /*a('.sinalmulti').style.background = 'green'
    a('.sinalmulti').style.color = 'white'*/
  }

  return (
    <div className='choose-sinal'>
      <button className='sinalsoma' onClick={sinalsoma}>SOMA</button>
      <button className='sinalsubt' onClick={sinalsubt}>SUBTRAÇÃO</button>
      <button className='sinaldivi' onClick={sinaldivi}>DIVISÃO</button>
      <button className='sinalmulti' onClick={sinalmulti}>MULTIPLICAÇÃO</button>
    </div>
  )
}

export default Choose
