import React from 'react'

function Choose() {
  const a = (cl) => document.querySelector(cl)

  const sinalsoma = () => {
    a('.sinal').innerHTML = '+'
  }

  const sinalsubt = () => {
    a('.sinal').innerHTML = '-'
  }

  const sinaldivi = () => {
    a('.sinal').innerHTML = '/'
  }

  const sinalmulti = () => {
    a('.sinal').innerHTML = 'x'
  }

  return (
    <div>
      <input type='radio' name='soma' onClick={sinalsoma} />
      <input type='radio' name='subt'  />
      <input type='radio' name='divi' />
      <input type='radio' name='multi' />
    </div>
  )
}

export default Choose
