function CleanLeft() {
    const a = (cl) => document.querySelector(cl)

    const [number1, setnumber1] = useState()
    const [innerHTMLNumber, setinnerHTMLNumber1] = useState()

    const cleanLeft = () => {
        let innerHTMLNumber1 = a('.number1').innerHTML.split('') 
        setinnerHTMLNumber1( innerHTMLNumber1 )

        /*let teste = ''
        for(let i in innerHTMLNumber1) {
            setnumber1( innerHTMLNumber1[i] )
            console.log(innerHTMLNumber1[i])
            teste =+ innerHTMLNumber1[i]
        }*/
        console.log(innerHTMLNumber1)
        a('.number1').innerHTML = innerHTMLNumber1.pop()
    }
  return (
    <button className='clean-left' onClick={cleanLeft}>x</button>
  )
}

export default CleanLeft
