import React from 'react'

function adição() {
        const a = (cl) => document.querySelector(cl)

        let sinai

        if(a('.sinal').innerHTML === '+') {
                let n1 = parseInt(a('.number1').value)
                let n2 = parseInt(a('.number2').value)
                let soma = n1 + n2
                document.querySelector('.res').innerHTML = soma
        }    
}

export default adição
