import React, { useState, useEffect } from 'react'
import '../components/Navbar.css'

function Navbar() {
  const calc = () => {
    let n1 = parseInt(document.querySelector('.number1').value)
    let n2 = parseInt(document.querySelector('.number2').value)
    let soma = n1 + n2
    document.querySelector('.res').innerHTML = soma
  }

  return (
    <div className="container">
      <input className='number1' type="number"/>+
      <p></p>
      <input className='number2' type="number"/>
      <button onClick={calc}>Calcular</button>
      <h1 className='res'>Resultado</h1>
    </div>
  )
}

export default Navbar
